function pageprocessorNotifications203(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);

  $scope.reachedLastPost = false;
  $scope.showLoadingBar = false;

  $rootScope.pushUrl = "https://push.kryptosmobile.com";
