      //alert($scope.lastPostObjectID);
      if (response.posts.length == 5) {
        $scope.showLoadingBar = true;
      } else {
        $scope.showLoadingBar = false;
      }
    }
    $.unblockUI();
  }).
  error(function(err) {
    $.unblockUI();
    //alert("err" + JSON.stringify(response));
  });

  $scope.openPost = function(id) {
    $rootScope.postInfo = $scope.pushFeed[id];
    //$location.path("/app/NotificationCenter/NotificationCenterPage2");
  }

  $scope.getNextPosts = function() {
    //alert($scope.lastPostObjectID);
    Feeddata.postObjectID = $scope.lastPostObjectID;

    //$.blockUI();
    //alert(FeedUrl);
    //alert(JSON.stringify(Feeddata));
    $http.post(FeedUrl, Feeddata).success(function(response) {
      //alert(JSON.stringify(response));

      if (response.posts.length == 0 && (response.postCounts > $scope.PushCounts)) {
        $scope.reachedLastPost = true;
        $scope.showLoadingBar = false;

        $scope.showNewPostsBtn = true;
        $('#newPostBtn').addClass('animated slideInDown');
        //navigator.notification.alert("No more posts found.", null, 'Post', 'Ok');

      } else if (response.posts.length == 0 && (response.postCounts == $scope.PushCounts)) {
        $scope.reachedLastPost = true;
        $scope.showLoadingBar = false;

      } else {
        // alert(response.postCounts +"      "+ $scope.PushCounts);
        if (response.postCounts > $scope.PushCounts) {
          $scope.showNewPostsBtn = true;
          $('#newPostBtn').addClass('animated slideInDown');
        }
        $scope.pushFeed = $scope.pushFeed.concat(response.posts);

        $scope.$apply();
        $scope.lastPostObjectID = response.posts[response.posts.length - 1]._id;
      }
      $.unblockUI();
    }).
    error(function(err) {
      $.unblockUI();
      //alert("err" + JSON.stringify(response));
    });

  }

  var chk_scroll = function(e) {

    var elem = $(e.currentTarget);
    //alert(elem[0].scrollHeight+" "+elem.scrollTop()+" "+elem.outerHeight());

    if (elem[0].scrollHeight - elem.scrollTop() == elem.outerHeight()) {
      //alert("bottom");
      if (!$scope.reachedLastPost) {
        $scope.getNextPosts();
      }
    }
  }


  $('.scrollable-content').bind('scroll', chk_scroll);

  var month = [];
  month[0] = "January";
  month[1] = "February";
  month[2] = "March";
  month[3] = "April";
  month[4] = "May";
  month[5] = "June";
  month[6] = "July";
  month[7] = "August";
  month[8] = "September";
  month[9] = "October";
  month[10] = "November";
  month[11] = "December";
  $scope.calculateTime = function(date) {
    var d = new Date(date);
    return month[d.getMonth()] + " " + d.getDate() + ", " + d.getFullYear();
  }
}

function pageprocessorNotifications203Page2(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);

  $scope.post = $rootScope.postInfo;

  var postId = $rootScope.postInfo.postId;


  $scope.calculateTimeDiff = function(d1) {

    var d2 = new Date();
    d1 = new Date(d1);
    var diff = d2 - d1;
    var seconds = diff / 1000;
    var numyears = Math.floor(seconds / 31536000);
    var numdays = Math.floor((seconds % 31536000) / 86400);
    var numhours = Math.floor(((seconds % 31536000) % 86400) / 3600);
    var numminutes = Math.floor((((seconds % 31536000) % 86400) % 3600) / 60);
    var numseconds = (((seconds % 31536000) % 86400) % 3600) % 60;
    if (numyears == -1) {
      return "Just Now";
    } else if (numyears != 0) {
      return (numyears == 0 ? '' : (numyears == 1 ? numyears + ' yr ' : numyears + " yrs ")) + " ago";
    } else if (numdays != 0) {
      return (numdays == 0 ? '' : (numdays == 1 ? numdays + ' day ' : numdays + " days ")) + " ago";
    } else if (numhours != 0) {
      return (numhours == 0 ? '' : (numhours == 1 ? numhours + ' hr ' : numhours + " hrs ")) + " ago";
    } else if (numminutes != 0) {
      return (numminutes == 0 ? '' : (numminutes == 1 ? numminutes + ' min ' : numminutes + " mins ")) + " ago";
    } else if (numseconds != 0) {
      return (numseconds == 0 ? '' : (numseconds == 1 ? numseconds + ' sec ' : parseInt(numseconds) + " secs ")) + " ago";
    }
  }
}

