function pageprocessorYouTube20(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  $.blockUI();
  $("#fixedFooterContent").html('');
  var url = "https://www.googleapis.com/youtube/v3/search?videoEmbeddable=true&order=date&part=snippet&channelId=" + pagedef.url + "&type=video&maxResults=50&key=AIzaSyCsgCy_wu22gBBf-jewPk76QN9mUzevkGw";
  $http.get(url).success(function(data) {
    $.unblockUI();
    if (data.items.length == 0) {
      if (window.device) {
        navigator.notification.alert(
          'No video found for given channel id.',
          null,
          '',
          'Ok'
        );
      } else
        alert('No video found for given channel id.');

      window.history.back();
      return;
    }
    for (var i = 0; i < data.items.length; i++) {
      data.items[i].playing = false;
    }

    $scope.videos = data.items;
    $scope.videos[0].playing = true;
    $rootScope.videos = data.items;
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    $scope.loadVideo(0);

  }).error(function(data) {
    $.unblockUI();
    if (window.device) {
      navigator.notification.alert(
        'Incorrect channel id.',
        null,
        '',
        'Ok'
      );
    } else
      alert('Incorrect channel id');
    window.history.back();
  });
  $scope.navigate = function(index) {
    if ($rootScope.webApp != undefined) {
      $scope.loadVideo(index);
    } else {
      $rootScope.index = index;
      $location.path("/app/YouTube20/YouTube20VideosDetails");
    }
  }


  $scope.loadVideo = function(index) {
    for (var i = 0; i < $scope.videos.length; i++) {
      $scope.videos[i].playing = false;
    }
    var video = $scope.videos[index];

    video.playing = true;
    var videoID = video.id.videoId;
    $scope.publishDate = videoID.publishedAt;
    var url = "https://www.googleapis.com/youtube/v3/videos?id=" + videoID + "&key=AIzaSyCsgCy_wu22gBBf-jewPk76QN9mUzevkGw&part=snippet,contentDetails,statistics,status";
    $http.get(url).success(function(data) {
      $scope.title = data.items[0].snippet.localized.title;
      $scope.publishDate = data.items[0].snippet.publishedAt;
      $scope.trustSrc = function() {
        return $sce.trustAsResourceUrl("https://www.youtube.com/embed/" + videoID + "?rel=0&autoplay=1");
      };
      $scope.description = data.items[0].snippet.localized.description;
    });
  }
}

function pageprocessorYouTube20VideosDetails(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  try {

    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    try {
      var onPause = function() {
        $("iframe").attr("src", $("iframe").attr("src"));
      };
      document.addEventListener("pause", onPause, false);
    } catch (e) {}
    $scope.showDetails = false;
    $scope.hideDetails = false;
    $scope.showDescription = function() {
      $scope.showDetails = true;
    }

    $scope.loadVideo = function(index) {
      for (var i = 0; i < $rootScope.videos.length; i++) {
        $rootScope.videos[i].playing = false;
      }

      $rootScope.videos[index].playing = true;
      var video = $rootScope.videos[index];
      var videoID = video.id.videoId;

      $scope.trustSrc = $sce.trustAsResourceUrl("https://www.youtube.com/embed/" + videoID + "?rel=0");

      var url = "https://www.googleapis.com/youtube/v3/videos?id=" + videoID + "&key=AIzaSyCsgCy_wu22gBBf-jewPk76QN9mUzevkGw&part=snippet,contentDetails,statistics,status";
      $http.get(url).success(function(data) {
        $scope.title = data.items[0].snippet.localized.title;
        $scope.publishDate = data.items[0].snippet.publishedAt;
        $scope.description = data.items[0].snippet.localized.description;
        $('.scrollable-content').animate({
                    scrollTop: 0
                }, 'slow');
      }).error(function(data) {
        $.unblockUI();
      });
    }

    $scope.loadVideo($rootScope.index);
  } catch (e) {
    console.log(e);
  }
}

