    $scope.searchCoursesforDates = function () {
        console.log("date changed");
        console.log($scope.searchbydate);
        if (!$scope.searchbydate) {
            return;
        }
        var curDate = new Date();
        var tomDate = new Date();
        tomDate.setDate(tomDate.getDate() + 1);
        var todayDate = new Date($scope.searchbydate);
        console.log("curdate:" + curDate);
        console.log("search date: " + todayDate);

        var dd = curDate.getDate();
        var mm = curDate.getMonth() + 1; //January is 0, so always add + 1

        var yyyy = curDate.getFullYear();
        if (dd < 10) {
            dd = '0' + dd
        }
        if (mm < 10) {
            mm = '0' + mm
        }
        curDate = yyyy + '/' + mm + '/' + dd;
        /********************************************************/
        dd = tomDate.getDate();
        mm = tomDate.getMonth() + 1; //January is 0, so always add + 1

        yyyy = tomDate.getFullYear();
        if (dd < 10) {
            dd = '0' + dd
        }
        if (mm < 10) {
            mm = '0' + mm
        }
        tomDate = yyyy + '/' + mm + '/' + dd;
        /********************************************************/

        dd = todayDate.getDate();
        mm = todayDate.getMonth() + 1; //January is 0, so always add + 1

        yyyy = todayDate.getFullYear();
        if (dd < 10) {
            dd = '0' + dd
        }
        if (mm < 10) {
            mm = '0' + mm
        }
        todayDate = yyyy + '/' + mm + '/' + dd;

        if (curDate == todayDate) {
            $scope.showdetailpage1 = false;
            $scope.showdetailpage3 = false;
            $scope.selectTab('tab1');
            $scope.searchbydate = "";
            console.log("this is todays date");
            return;
        } else if (tomDate == todayDate) {
            $scope.showdetailpage2 = false;
            $scope.showdetailpage3 = false;
            $scope.selectTab('tab2');
            $scope.searchbydate = "";
            console.log("this is tom date");
            return;
        }

        $scope.sourceindicator = "D";
        $scope.showdetailpage3section2 = false;
        $scope.showdetailpage3section3 = true;
        $rootScope.searchbydate = $scope.searchbydate;
        $rootScope.studentschedule = $scope.studentschedule;
        $scope.loadgrouppage();
        //$location.path("/app/studentschedule/studentschedulePage3");
    }

    $rootScope.back = function () {
        if ($scope.selTab == 1) {
            if ($scope.showdetailpage1) {
                $scope.showdetailpage1 = false;
                //$rootScope.showlogin = true;
            } else {
                //$rootScope.showlogin = true;
                $rootScope.leftToRight = true;
                $location.path("/app/RegistrationStatus/RegistrationStatus");
            }
        } else if ($scope.selTab == 2) {
            if ($scope.showdetailpage2) {
                $scope.showdetailpage2 = false;
                //$rootScope.showlogin = true;
            } else {
                //$rootScope.showlogin = true;
                $rootScope.leftToRight = true;
                $location.path("/app/RegistrationStatus/RegistrationStatus");
            }
        } else if ($scope.selTab == 3) {
            counter = 0;
            if ($scope.showdetailpage3) {
                console.log("in seltab:");
                console.log("$scope.showdetailpage3section2=" + $scope.showdetailpage3section2);
                console.log("$scope.showdetailpage3section3=" + $scope.showdetailpage3section3);
                console.log("$scope.sourceindicator=" + $scope.sourceindicator);
                if ($scope.sourceindicator == "D") {
                    if ($scope.showdetailpage3section3) {
                        $scope.showdetailpage3section3 = false;
                        //$rootScope.showlogin = true;
                    } else if ($scope.showdetailpage3section2) {
                        $scope.showdetailpage3section2 = false;
                        $scope.showdetailpage3section3 = true;
                        $rootScope.showlogin = false;
                    } else {
                        //$rootScope.showlogin = true;
                        $scope.selectedcourse = "";
                        $scope.searchbydate = "";
                        $rootScope.leftToRight = true;
                        $location.path("/app/RegistrationStatus/RegistrationStatus");
                    }
                } else if ($scope.sourceindicator == "C") {
                    if ($scope.showdetailpage3section2) {
                        $scope.showdetailpage3section2 = false;
                        $scope.showdetailpage3section3 = false;
                        //$rootScope.showlogin = true;
                    } else {
                        //$scope.showlogin = true;
                        $rootScope.leftToRight = true;
                        $location.path("/app/RegistrationStatus/RegistrationStatus");
                    }
                }
                console.log("you are here...");
                $scope.searchbydate = "";
                $scope.selectedcourse = "";
            }
        }
    }

    $scope.openDate = function () {
        var options = {
            date: new Date(),
            mode: 'date'
        };


        datePicker.show(options, onSuccess, onError);
    };

    $scope.openOption = function (value) {
        if (value == 'course') {
            $('#searchcourse').focus();
        } else {
            $scope.openDate();
        }

    };


    function onSuccess(date) {

        $scope.searchbydate = date;
        $scope.searchCoursesforDates();
    }

    function onError(error) { // Android only
    }
}

function pageprocessorstudentschedulePage2(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    
    var monthNames = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];

    $scope.formatDate = function(dt) {
    	if (!dt) {
    		return "";
    	}
    	var tempDt = new Date(dt);
    	var month = monthNames[tempDt.getMonth()];
    	var retVal = month +  " " + tempDt.getDate() + ", " + tempDt.getFullYear();
    	return retVal;
    }

    $scope.todayschedule = $rootScope.todayschedule;
    $scope.todayschedule.START_DATE = $scope.formatDate( $scope.todayschedule.START_DATE);
    $scope.todayschedule.END_DATE = $scope.formatDate( $scope.todayschedule.END_DATE);
}

