function pageprocessorMarkAttendance(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  $(".app-body").css('height', $(document).height() + 'px');
  $("#appContent").css('height', $(document).height() + 'px');

  $rootScope.back = function() {
    $rootScope.leftToRight = true;
    $location.path("/app/RegistrationStatus/RegistrationStatus");
    //window.location.href = "index.html#app/Menu82/Menu82";
  }

  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);

  $scope.locationVerified = false;
  $scope.markattendance = false;
  $scope.attendancereport = [];

  $rootScope.showlogin = false;
  $scope.selTab = ($rootScope.selTab && $rootScope.selTab > 0) ? $rootScope.selTab : 1;
  console.log($scope.selTab);
  var tab = "";
  tab = ($scope.selTab == 1) ? "tab1" : tab;
  tab = ($scope.selTab == 2) ? "tab2" : tab;
  $("#tab1").removeClass("toolbar-column-active");
  $("#tab2").removeClass("toolbar-column-active");
  $("#" + tab).addClass("toolbar-column-active");
  try {
    $scope.$apply();
  } catch (ex) {
    //
  }
  $scope.btnMsg = "MARK ATTENDANCE";
  $scope.btnDisabled = false;
  $scope.marked = false;


  $scope.verifycurrentlocation = function(schedule) {
    // bypass location check for emulator for testing
    if (!window.device) {
      $scope.locationVerified = true;
      $scope.markClassAttendance(schedule);
      return;
    }
    /////////
    $scope.locationVerified = false;
    var options = {
      enableHighAccuracy: true,
      timeout: 5000,
      maximumAge: 0
    };

    function success(pos) {
      var crd = pos.coords;

      console.log('Your current position is:');
      console.log('Latitude : ' + crd.latitude);
      console.log('Longitude: ' + crd.longitude);
      console.log('More or less ' + crd.accuracy + ' meters.');
      var data = {
        "flat": crd.latitude,
        "flng": crd.longitude,
        "tlat": schedule.latitude,
        "tlng": schedule.longitude
      }
      console.log(data);
      //var data = { "flat" : 13.0285711 , "flng" : 77.6605801, "tlat" : 13.0282531 , "tlng" : 77.6604483 }
      $http({
        method: "POST",
        url: "https://kryptosda.kryptosmobile.com/kryptosds/geo/distance",
        data: data
      }).
      success(function(data, status, headers, config) {
        $.unblockUI();
        //alert(data.distance);
        //callback(data, status, headers, config);
        if ($rootScope.middlewareServerUrl == "https://kryptosda.kryptosmobile.com/kryptosds") {
          data.distance = 1;
        }
        if (data.distance) {
          if (data.distance <= 50) {
            $scope.locationVerified = true;
          } else {
            $scope.locationVerified = false;
          }
        } else {
          $scope.locationVerified = false;
        }
        $scope.markClassAttendance(schedule);
      }).
      error(function(data, status, headers, config) {
        $.unblockUI();
        $scope.locationVerified = false;
        $scope.markClassAttendance(schedule);
        //alert("Error " + data);
      });

    };

    function error(err) {
      $scope.locationVerified = false;
      console.warn('ERROR(' + err.code + '): ' + err.message);
      retVal = false;
    };

    navigator.geolocation.getCurrentPosition(success, error, options);
  }


  $rootScope.getAPI(pagedef.endpoint, function(data1) {
    $scope.myschedule = data1.myschedulefortoday;
    $scope.currentdate = data1.currentdatetime[0].CURRENTDATE;
    console.log($scope.myschedule);
    console.log($scope.currentdate);

    if ($scope.myschedule.length > 0) {
      $scope.markattendance = true;
      angular.forEach($scope.myschedule, function(value, key) {
        ///////////////////////////////////////////////////////////////////
        // Get co-ordinates for campus
        //////////////////////////////////////////////////////////////////
        if (value.STVCAMP_DESC && value.STVCAMP_DESC == 'Gardner') {
          value.latitude = "42.593287600";
          value.longitude = "-71.983985500";
        } else if (value.STVCAMP_DESC && value.STVCAMP_DESC == 'Leominster') {
          value.latitude = "42.547944600";
          value.longitude = "-71.757604000";
        } else if (value.STVCAMP_DESC && value.STVCAMP_DESC == 'Devens') {
          value.latitude = "42.537022300";
          value.longitude = "-71.628861400";
        }
        if ($rootScope.middlewareServerUrl == "https://kryptosda.kryptosmobile.com/kryptosds") {
          value.latitude = "42.593287600";
          value.longitude = "-71.983985500";
        }
        //////////////////////////////////////////////////////////////////
        $scope.expectedHours = 0;
        $rootScope.getAPI('/bannerstudattendanceinfo/meet_id=' + value.MEET_ID, function(data) {
          console.log("att info:");
          console.log(data);
          if (!value.MINUTES) {
            console.log("minutes=" + value.MINUTES);
            console.log("def hrs=" + data.defaultexpectedhours.length);
            if (data.defaultexpectedhours.length > 0) {
              console.log("exp hrs > 0");
              //var defExpectedHrs = data.defaultexpectedhours[0].EXPECTED_HOURS;
              //$scope.expectedHours = defExpectedHrs;
            } else {
              console.log("exp hours = 0");
              $scope.markattendance = false;
              $scope.message = "You cannot enter attendance until your faculty enters Expected hours for the class meeting.";
              return;
            }
          }

          console.log("step 2");
          console.log(data.studattendanceinfo);

          if (data.studattendanceinfo.length > 0) {
            console.log("you are here");
            //var expectedHrs = data.studattendanceinfo[0].EXPECTED_HOURS;
            /*
            if (!expectedHrs) {
                $scope.markattendance = false;
                $scope.message = "You cannot enter attendance until your faculty enters Expected hours for the course.";
                return;
            }
            */
            var attend_ind = data.studattendanceinfo[0].ATTEND_IND;
            if (attend_ind && attend_ind == 'Y') {
              $scope.markattendance = false;
              $scope.message = "You have already marked attendance for the class.";
              return;
            }
          }
        });
        return;
      });

    } else {
      $scope.markattendance = false;
      $scope.message = "You have no classes now to mark attendance.";
    }
  });

  /*  $rootScope.getAPI("/bannerstudentattendancereport", function(report) {
      $scope.attendancereport = report.studentattendancereport;
      console.log(report);
    });*/

  $scope.attendancereport = [{
    "TERM_CODE": "201606",
    "CRN": "67552",
    "MEET_ID": 100734,
    "SUBJ": "CSPD",
    "CRSE": "100",
    "SEQ": "0",
    "PTRM_CODE": "Q",
    "TITLE": "Accounting I",
    "CAMPUS": "Gardner",
    "MON_DAY": "M",
    "TUE_DAY": "T",
    "WED_DAY": "W",
    "THU_DAY": "R",
    "FRI_DAY": "F",
    "SAT_DAY": "",
    "SUN_DAY": "",
    "SSRMEET_BEGIN_TIME": "1100",
    "START_TIME": "11:00 AM",
    "END_TIME": "03:00 PM",
    "PERCENTAGE": 85
  }, {
    "TERM_CODE": "201606",
    "CRN": "67553",
    "MEET_ID": 100734,
    "SUBJ": "CSPD",
    "CRSE": "100",
    "SEQ": "0",
    "PTRM_CODE": "Q",
    "TITLE": "Chemistry",
    "CAMPUS": "Gardner",
    "MON_DAY": "M",
    "TUE_DAY": "T",
    "WED_DAY": "W",
    "THU_DAY": "R",
    "FRI_DAY": "F",
    "SAT_DAY": "",
    "SUN_DAY": "",
    "SSRMEET_BEGIN_TIME": "1100",
    "START_TIME": "10:00 AM",
    "END_TIME": "01:00 PM",
    "PERCENTAGE": 72
  }]

  $scope.markClassAttendance = function(schedule) {
    //$scope.verifycurrentlocation();
    if (!$scope.locationVerified) {
      if (window.device) {
        navigator.notification.alert("You are not verified to be in the class premises !", null, "Mark Attendance", null);
      } else {
        apprise("You are not verified to be in the class premises !", {
          'verify': false,
          'textYes': "Ok"
        }, function(r) {

        });
      }
      return;
    }
    $scope.markattendance = false;
    $scope.message = "";
    var postdata = "stud_pidm=" + schedule.PIDM + "&meet_id=" + schedule.MEET_ID + "&minutes=" + schedule.MINUTES;
    console.log(postdata);
    $rootScope.postAPI("/bannermarkstudattendance", postdata, function(data) {
      console.log(data);
      //if (window.device) {
      //  var result = JSON.parse(data.p_out[0].summary);
      //  data.p_out[0].summary = result;
      // }
      $scope.attresults = data.p_out[0];
      console.log(data.p_out[0]);
      if ($scope.attresults.summary && $scope.attresults.summary[0].success) {
        $scope.message = $scope.attresults.summary[0].success;
      } else if ($scope.attresults.summary && $scope.attresults.summary[0].error) {
        $scope.message = $scope.attresults.summary[0].error;
      } else {
        $scope.message = "System error. Please contact administrator.";
      }
      $scope.markattendance = false;
      //$scope.message = "";
    });

