function pageprocessorContactSupport(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);

  if (window.device) {

  }
  pagedef.url = "https://www.allen.ac.in/contactus.asp"
  var iabRef = window.open(pagedef.url, "_blank", "location=yes,hidden=no,enableViewportScale=yes,clearcache=yes");
  window.history.back();
  var hideBlockUi = function() {
    $.unblockUI();
    iabRef.show();
  };
  var iabClose = function(data) {
    $.unblockUI();
    iabRef.removeEventListener("exit", iabClose);

    window.location.href = "index.html#app/Menu82/Menu82";
    //$location.path("/app/SocialNetworking/SocialNetworking");
  };
  iabRef.addEventListener("exit", iabClose);
  var loadStop = function(data) {
    hideBlockUi();
    iabRef.removeEventListener("loadstop", loadStop);
  };
  iabRef.addEventListener("loadstop", loadStop);
}
